import {useState, useEffect} from 'react';
import {Statistic, Image, Divider, Row, Col, Spin} from 'antd';

import {prepareDB, getMovieById} from '../../core/db';
import {fetchHDFSRead} from '../../core/api';
import VirtualTable from '../lib/virtualtable';

import "./job-results.css";

function getRowRenderer({list}) {
  return function({key, index, style}) {
    const [movieId, rating] = list[index];
    const movie = getMovieById(movieId);

    return (
      <ResultRow
        movieId={movieId}
        movie={movie}
        rating={rating}
        key={key}
        style={style}
      />
    );
  }
}

function ResultRow({movie, rating, style}) {
  const [tmdb, setTmdb] = useState();
  
  useEffect(async () => {
    const tmdbRes = await fetch(`https://api.themoviedb.org/3/movie/${movie.tmdbId}?api_key=fa4169bbc74fdb9b73c7e90bc9786416`);
    setTmdb(await tmdbRes.json());
  }, [movie]);

  if (!tmdb) {
    return (
      <div className="job-result-spin" style={style}>
        <Spin size="default" />
      </div>
    );
  }

  return (
    <div className="job-result-row" style={style}>
      <Image
        preview={{src:`https://image.tmdb.org/t/p/w780${tmdb.poster_path}`}}
        className="job-result-poster"
        width="154px"
        height="231px"
        src={`https://image.tmdb.org/t/p/w154${tmdb.poster_path}`}
        fallback="data:image/png;base64,"
      />
      <div className="job-result-details">
        <h2>{tmdb.title}</h2>
        <Row>
          <Col flex="120px">
            <Statistic title="Our Rating" value={rating.toFixed("3")} />
          </Col>
          <Col flex="120px">
            <Statistic title="TMDB Rating" value={tmdb.vote_average} />
          </Col>
          <Col flex="120px">
            <Statistic title="TMDB Popularity" value={tmdb.popularity} />
          </Col>
        </Row>
        <p style={{paddingTop:'10px'}}>{tmdb.overview}</p>
      </div>
    </div>
  );
}

export default function JobResults({jobDetails}) {
  const [metaData, setMetaData] = useState({});

  if (!jobDetails.done) {
    return (
      <div>Job result will be here when job completed.</div>
    );
  }

  useEffect(async () => {
    await prepareDB();
    let output = await fetchHDFSRead(`${jobDetails.output}/part-r-00000`);
    output = output.map(o => o.split(',').map(Number))
      .filter(([,r]) => r==r)
      .sort(([,a], [,b]) => b - a);

    setMetaData({
      ready: true,
      output,
      totalEntry: output.length,
      maxValue: output[0][1],
      minValue: output[output.length-1][1],
    });
  }, [jobDetails]);

  if (!metaData.ready) {
    return (
      <div className="spin-center">
        <Spin size="large" />
      </div>
    );
  }

  return (
    <>
      <Row justify="space-between">
        <Col span={8}>
          <Statistic title="Result Count" value={metaData.totalEntry} />
        </Col>
        <Col span={8}>
          <Statistic title="Max Value" value={metaData.maxValue.toFixed('3')} />
        </Col>
        <Col span={8}>
          <Statistic title="Min Value" value={metaData.minValue.toFixed('3')} />
        </Col>
      </Row>
      <Divider></Divider>
      <VirtualTable
        rowHeight={251}
        heightMargin={400}
        getRowRenderer={getRowRenderer}
        list={metaData.output}
      />
    </>
  );
}